import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Button} from 'react-bootstrap';


class EditTodo extends Component {
  handleClick = (e) => {
    e.preventDefault();
    const newTodo = this.getTodo.value;
    const data = {
      newTodo
    }
    this.props.dispatch({ type: 'UPDATE_TODO', id: this.props.id, data: data })
  }
  render() {
    return (
      <div>
        <form onSubmit={this.handleClick}>
          <Button data-toggle="collapse" data-target="#collapseEdit"> </Button>
          <input required type="text" ref={(input) => this.getTodo = input} defaultValue={this.props.text} placeholder="Edit Todo List" />
          <Button style={{marginLeft: '10px'}} className="btn btn-info" size="sm" type="submit">Update</Button>
        </form>
      </div>
    );
  }
}
export default connect()(EditTodo);
