import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Button} from 'react-bootstrap';
import {Collapse} from 'react-bootstrap'


class EditTodo extends Component {
  constructor(props, context) {
    super(props, context);
    this.state = {
      open: false
    }
  }

  handleClick = (e) => {
    e.preventDefault();
    const newTodo = this.getTodo.value;
    const data = {
      newTodo
    }
    this.props.dispatch({ type: 'UPDATE_TODO', id: this.props.id, data: data })
  }
  render() {
    const {open} = this.state
    const isCompleted = this.props.completed
    return (
      <div>
        {isCompleted ? (<Button disabled> Edit Todo</Button>):(<Button onClick={() => this.setState({ open: !open })}
                                                                aria-controls="example-collapse-text" aria-expanded={open}
                                                                size="sm">Edit Todo</Button>)}


      </div>
    );
  }
}
export default connect()(EditTodo);
