import React from 'react'
import PropTypes from 'prop-types'
import DeleteTodo from '../containers/DeleteTodo'
import DuplicateTodo from '../containers/DuplicateTodo'

export default class Todo extends React.Component {

  render() {
    return (
        <li
            onClick={this.props.onClick}
        >
          <span style={{textDecoration: this.props.completed ? 'line-through' : 'none'}}>{this.props.text} </span>
          <span className="badge c1-bg badge-pill">
            <DeleteTodo id={this.props.id} />
          </span>
        </li>
    )
  }
}

Todo.propTypes = {
  onClick: PropTypes.func.isRequired,
  completed: PropTypes.bool.isRequired,
  text: PropTypes.string.isRequired,
  id: PropTypes.number.isRequired
}
