import React from 'react'
import {connect} from 'react-redux'
import {addTodo, duplicateTodo} from '../actions'

let DuplicateTodo = ({dispatch}) => {
  let textDuplicate
  return (
      <div>
        <form onSubmit={e => {
          e.preventDefault()
          if (!textDuplicate.value.trim()) {
            return
          }
          dispatch(addTodo(textDuplicate.value))
          textDuplicate.value = ''
        }}>
          <input ref={node => textDuplicate = node} />
          <button type="submit">
            Add Todo
          </button>
        </form>
      </div>
  )
}

DuplicateTodo = connect()(DuplicateTodo)

export default DuplicateTodo
