import React, {Component} from 'react';
import { connect } from 'react-redux';
import {editTodo} from '../actions'

let edit = ({text, dispatch}) => {
  dispatch(editTodo(text))
  console.log(dispatch(editTodo(text)))
}

class EditTodo extends Component {
  constructor() {
    super()
    this.state = {
      show: false
    }
  }
  handleClick() {
    this.setState ({
      show: true
    })
  }
  render() {
    return (
        <div>
          {this.state.show ? null :
            <div>
              <input type='text' defaultValue={edit}/>
            </div>
          }
          <button onClick={() => this.handleClick()}> Edit Todo </button>
        </div>
    )
  }
}

EditTodo = connect() (EditTodo)

export default EditTodo
